import React from 'react';
import './Current-training-tab.css'
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import Button from '@material-ui/core/Button';
import {exercises} from '../../../Exercises';
import CurrentTraining from '../current-training-timer/Current-training-timer';


const CurrentTrainingTab = ({loading, progress}) => {

  const [exercise, setExercise] = React.useState('');
  const [startExercise, setStartExercise] = React.useState(false);


  const handleExerciseChange = (event) => {
      if(event.target.value !== 0) {
        setExercise(event.target.value);
      }
      return;


  };

  const CloseChooseExercise = () => {
    setStartExercise(false);
  };

  const OpenChoosedExercise = () => {
    setStartExercise(true);
  };



    return(
        <div className="selectEx">
        <Button onClick={OpenChoosedExercise} >
          Open the select
        </Button>
        <FormControl >
          <InputLabel >Exercises</InputLabel>
          <Select
            labelId="demo-controlled-open-select-label"
            id="demo-controlled-open-select"
            open={startExercise}
            onClose={CloseChooseExercise}
            onOpen={OpenChoosedExercise}
            value={exercise}
            onChange={handleExerciseChange}>    
          {exercises.map(ex => {
              return  <MenuItem value={ex.id} > {ex.id} </MenuItem>
          })} 
          </Select>
        </FormControl>
       <CurrentTraining
        exercise={exercise}
        loading={loading}
        progress={progress}
         />
      </div>
    ) 
}

export default CurrentTrainingTab;
