import React, { useRef } from 'react';
import './Current-training-timer.css'
import CircularProgress from '@material-ui/core/CircularProgress';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import Button from '@material-ui/core/Button';
import { exercises } from '../../../Exercises';


const CurrentTraining = ({exercise}) => {

    const [progress, setProgress] = React.useState(0);
    const [loading, setLoading] = React.useState(false);

    const interval = useRef();

    const CircularProgressWithLabel = (props) => {
        return (
          <Box position="relative" display="inline-flex">
            <CircularProgress variant="static" {...props} />
            <Box
              top={0}
              left={0}
              bottom={0}
              right={0}
              position="absolute"
              display="flex"
              alignItems="center"
              justifyContent="center"
            >
              <Typography variant="caption" component="div" color="textSecondary">{`${Math.round(
                props.value,
              )}%`}</Typography>
            </Box>
          </Box>
        );
      }
    React.useEffect(() => {
      let curEx = exercises.find( ex => ex.id === exercise)
      if(curEx){
        let duration = curEx.duration / 100 * 1000;
        console.log(duration)

         interval.current = null;
        if(loading) {
            interval.current = setInterval(()=> {
            setProgress((prevProgress) => (prevProgress >= 100 ? 10 : prevProgress + 1))

        }, duration) 
        if (progress >= 100) {
            setLoading(false)
            clearInterval(interval.current);
          }
        }  
        return () => clearInterval(interval) }
      }, [loading, progress, exercise ]);


      const startTraining = () => {
          setLoading(true)
      }

      const cancelTraining = () => {
        console.log(interval)
          clearInterval(interval.current)
      }



    return(
        <div className="timer">
            <div>
           {loading && <CircularProgressWithLabel  size="12rem" value={progress} />}
           </div>
           {!loading ?<Button variant="contained" color="primary" onClick={startTraining}> 
            Start
           </Button>:  <Button variant="contained" color="primary" onClick={cancelTraining}> 
            Pause
    </Button> }
        </div>


    )

}

export default CurrentTraining;
