import React from 'react';
import CircularProgress from '@material-ui/core/CircularProgress';
import {exercises} from '../../../Exercises';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';

const CurrentTraining = () => {

    const [progress, setProgress] = React.useState(0);
    const [loading, setLoading] = React.useState(false);

    const CircularProgressWithLabel = (props) => {
        return (
          <Box position="relative" display="inline-flex">
            <CircularProgress variant="static" {...props} />
            <Box
              top={0}
              left={0}
              bottom={0}
              right={0}
              position="absolute"
              display="flex"
              alignItems="center"
              justifyContent="center"
            >
              <Typography variant="caption" component="div" color="textSecondary">{`${Math.round(
                props.value,
              )}%`}</Typography>
            </Box>
          </Box>
        );
      }

    React.useEffect(() => {
        const duration = exercises.map(d => {
            return d.duration / 100 * 10
        })
        const timer = setInterval(()=> {
            setProgress((prevProgress) => (prevProgress >= 100 ? 1 : prevProgress + 10))
            if(progress >= 100) {
                setLoading(false)
            }
        }, 1000)
        return () => {clearInterval(timer)}   
      }, [progress]);

      const startTraining = () => {
          setLoading(true)
      }


    return(
        <div className="timer">
           {loading && <CircularProgressWithLabel variant="static" value={progress} />}
           <button onClick={startTraining}>start</button>
        </div>


    )

}

export default CurrentTraining;
