import React from 'react';
import './Current-training-tab.css'
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import Button from '@material-ui/core/Button';
import {exercises} from '../../../Exercises';


const CurrentTrainingTab = () => {

  const [exercise, setExercise] = React.useState(cho(selectedId));
  const [open, setOpen] = React.useState(false);


  cho = (selectedId) => {
      return exercises.find(ex => ex.id === selectedId )

  }
  const handleChange = (event) => {
    setExercise(event.target.value);

  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleOpen = () => {
    setOpen(true);
  };



    return(

        <div className="selectEx">
        <Button  >
          Open the select
        </Button>
        <FormControl >
          <InputLabel id="demo-controlled-open-select-label">Exercises</InputLabel>
          <Select
            labelId="demo-controlled-open-select-label"
            id="demo-controlled-open-select"
            open={open}
            onClose={handleClose}
            onOpen={handleOpen}
            value={exercise}
            onChange={handleChange}
          >
              {exercises.map(ex => {
                return <MenuItem key={ex.id} value={ex.id}> {ex.name} </MenuItem>

              })}

          </Select>
        </FormControl>
      </div>
    )



}

export default CurrentTrainingTab;
