import React from 'react';
import './Current-training-timer.css'
import CircularProgress from '@material-ui/core/CircularProgress';
import {exercises} from '../../../Exercises';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import Button from '@material-ui/core/Button';


const CurrentTraining = () => {

    const [progress, setProgress] = React.useState(0);
    const [loading, setLoading] = React.useState(false);

    const timer = React.useRef()

    const CircularProgressWithLabel = (props) => {
        return (
          <Box position="relative" display="inline-flex">
            <CircularProgress variant="static" {...props} />
            <Box
              top={0}
              left={0}
              bottom={0}
              right={0}
              position="absolute"
              display="flex"
              alignItems="center"
              justifyContent="center"
            >
              <Typography variant="caption" component="div" color="textSecondary">{`${Math.round(
                props.value,
              )}%`}</Typography>
            </Box>
          </Box>
        );
      }

    React.useEffect(() => {
        const duration = exercises.map(d => {
            return d.duration / 100 * 10
        })
        timer.current = setInterval(()=> {
            setProgress((prevProgress) => (prevProgress >= 100 ? 1 : prevProgress + 10))
            if(progress >= 100) {
                setLoading(false)
            }
        }, 1000)
        return () => {clearInterval(timer.current)}   
      }, [progress]);

      const startTraining = () => {
          setLoading(true)
      }

      const stopTraining = () => {
        setLoading(false)
        clearInterval(timer.current)

    }


    return(
        <div className="timer">
            <div>
           {loading && <CircularProgressWithLabel  size="12rem" value={progress} />}
           </div>
           <Button variant="contained" color="primary" onClick={startTraining}>start</Button>
        </div>


    )

}

export default CurrentTraining;
