import React from 'react';
import './Current-training-timer.css'
import CircularProgress from '@material-ui/core/CircularProgress';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import Button from '@material-ui/core/Button';
import { exercises } from '../../../Exercises';


const CurrentTraining = ({exercise}) => {

    const [progress, setProgress] = React.useState(0);
    const [loading, setLoading] = React.useState(false);

    const currentEx = exercises.find( ex => ex.id === exercise);

    const CircularProgressWithLabel = (props) => {
        return (
          <Box position="relative" display="inline-flex">
            <CircularProgress variant="static" {...props} />
            <Box
              top={0}
              left={0}
              bottom={0}
              right={0}
              position="absolute"
              display="flex"
              alignItems="center"
              justifyContent="center"
            >
              <Typography variant="caption" component="div" color="textSecondary">{`${Math.round(
                props.value,
              )}%`}</Typography>
            </Box>
          </Box>
        );
      }
    React.useEffect(() => {
      return currentEx.duration
        let interval = null;
        if(loading) {
            interval = setInterval(()=> {
            setProgress((prevProgress) => (prevProgress >= 100 ? 10 : prevProgress + 10))

        }, 1000) 
        if (progress >= 100) {
            setLoading(false)
            clearInterval(interval);
          }
        }  
        return () => clearInterval(interval)
      }, [loading, progress, ]);


      const startTraining = () => {
          setLoading(true)
      }

      const stopTraining = () => {
          setLoading(false)
      }



    return(
        <div className="timer">
            <div>
           {loading && <CircularProgressWithLabel  size="12rem" value={progress} />}
           </div>
           {!loading ?<Button variant="contained" color="primary" onClick={startTraining}> 
            Start
           </Button>:  <Button variant="contained" color="primary" onClick={stopTraining}> 
            Pause
    </Button> }
        </div>


    )

}

export default CurrentTraining;
