import React, { useEffect, useState, useContext } from 'react';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import { AuthContext } from '../../auth/AuthContext';
import { Button } from '@material-ui/core';
import firebase from 'firebase/app';



const PastTrainingTable = () => {

  const [finishedExercises, setFinishedExercises] = useState();
  const {uid} = useContext(AuthContext);
  let database = firebase.database();

  useEffect(() => {
    fetch('https://training-app-d460e.firebaseio.com/exercises.json')
    .then(result => result.json())
    .then(responseData => {
      const dataArray = []
      for(const key in responseData) {
        dataArray.push({
          id: key,
          exUserId: responseData[key].exUserId,
          name: responseData[key].name,
          duration: responseData[key].duration,
          calories: responseData[key].calories,
          status: responseData[key].status,
          date: responseData[key].date
        })
      }
      setFinishedExercises(dataArray)
    })

    .catch(err => console.log(err))
      }, []);

  const onDeleteExercise = (exerciseId) => {
    let userRef = database.ref('exercises/' + exerciseId);
    userRef.remove()

  }

    return (
        <TableContainer component={Paper}>
          {console.log(finishedExercises)}
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Exercise</TableCell>
                <TableCell align="right">Duration </TableCell>
                <TableCell align="right">Date </TableCell>
                <TableCell align="right"> Calories </TableCell>
                <TableCell align="right"> Status</TableCell>
                <TableCell align="right"> Action</TableCell>

              </TableRow>
            </TableHead>
            <TableBody>
              { finishedExercises && uid && finishedExercises.map((row) => (

               uid === row.exUserId ?
                <TableRow key={row.id}>

                  <TableCell component="th" scope="row">
                    {row.name}
                  </TableCell>
                  <TableCell align="right">{row.duration}</TableCell>
                  <TableCell align="right">{row.date}</TableCell>
                  <TableCell align="right">{row.calories}</TableCell>
                  <TableCell align="right">{row.status}</TableCell>
                  <TableCell align="right"><Button onClick={onDeleteExercise(row.id)}>Delete</Button></TableCell>

                </TableRow> : null
              )) }
            </TableBody>
          </Table>
        </TableContainer>
      );

}

export default PastTrainingTable;
