import React, { useRef } from 'react';
import './Current-training-timer.css'
import CircularProgress from '@material-ui/core/CircularProgress';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import Button from '@material-ui/core/Button';
import { exercises } from '../../../Exercises';
import CancelModal from '../../../cancelModel/CancelModel';


const CurrentTraining = ({exercise}) => {

    const [progress, setProgress] = React.useState(0);
    const [loading, setLoading] = React.useState(false);
    const [openCancelModal, setOpenCancelModal] = React.useState(false);

    const handleOpenCancelModal = () => {
      setOpenCancelModal(true);
    };

  const handleCloseCancelModal = () => {
    setOpenCancelModal(false)
  };

    const interval = useRef(0);

    const CircularProgressWithLabel = (props) => {
        return (
          <Box position="relative" display="inline-flex">
            <CircularProgress variant="static" {...props} />
            <Box
              top={0}
              left={0}
              bottom={0}
              right={0}
              position="absolute"
              display="flex"
              alignItems="center"
              justifyContent="center"
            >
              <Typography variant="caption" component="div" color="textSecondary">{`${Math.round(
                props.value,
              )}%`}</Typography>
            </Box>
          </Box>
        );
      }
    React.useEffect(() => {
      let curEx = exercises.find( ex => ex.id === exercise)
      if(curEx){
        let duration = curEx.duration / 100 * 1000;


        if(loading) {
            interval.current = setInterval(()=> {
            setProgress((prevProgress) => (prevProgress >= 100 ? 10 : prevProgress + 1))

        }, duration) 
        if (progress >= 100) {
            setLoading(false)
            clearInterval(interval.current);
          }
        }  
        return () => clearInterval(interval.current) }
      }, [loading, progress, exercise ]);


      const startTraining = () => {
          setLoading(true)
      }

      const cancelTraining = () => {
        clearInterval(interval.current);
        setProgress(0)
        setLoading(false);
      }



    return(
      <React.Fragment>
        { loading &&  <CancelModal
           open={openCancelModal}
           cancelTraining={cancelTraining}
           handleClose={handleCloseCancelModal}
           handleOpen={handleOpenCancelModal}
        /> }
        <div className="timer">
            <div>
           {loading && <CircularProgressWithLabel  size="12rem" value={progress} />}
           </div>
           {!loading ?<Button variant="contained" color="primary" onClick={startTraining}> 
            Start
           </Button>:  <Button variant="contained" color="primary" onClick={handleOpenCancelModal}> 
            Cancel
    </Button> }

        </div>


      </React.Fragment>


    )

}

export default CurrentTraining;
