import React, {useRef, useState} from 'react';
import SwipeableViews from 'react-swipeable-views';
import { useTheme } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import PastTrainingTable from '../past-training-table/Past-training-table';
import {exercises} from '../../Exercises';
import CurrentTrainingTab from '../current-training/current-training-tab/CurrentTrainingTab';
import { TabPanel } from '../../TabPanel/TabPanel';





export default function TrainingTab() {
  const theme = useTheme();

  const interval = useRef(0);
  const finishedExercises = useRef([])
  const currentExercise = useRef('')

  const [value, setValue] = useState(0);
  const [progress, setProgress] = useState(0);
  const [exercise, setExercise] = useState('');
  const [loading, setLoading] = useState(false);

  const handleExerciseChange = (event) => {
    if(event.target.value !== 0) {
      setExercise(event.target.value);
    }
    return;
  };

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  const handleChangeIndex = (index) => {
    setValue(index);
  };

  React.useEffect(() => {
    currentExercise.current = exercises.find( ex => ex.id === exercise)
    if(currentExercise.current){
      let duration = currentExercise.current.duration / 100 * 1000;
      if(loading) {
          interval.current = setInterval(()=> {
          setProgress((prevProgress) => (prevProgress >= 100 ? 10 : prevProgress + 1))

      }, duration) 
      if (progress >= 100) {
          setLoading(false)
          clearInterval(interval.current);
          const data = {
            ...currentExercise.current,
            id: currentExercise.current.name, 
            duration: currentExercise.current.duration * (progress / 100),
            calories: currentExercise.current.calories * (progress / 100),
            date: ( new Date() ).toLocaleDateString().split("."),
            status: "Completed"
          };
          fetch('https://training-app-d460e.firebaseio.com/exercises.json', {
            method: "POST",
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify(data)

        }).then(result => {
           return result.json();
        })
        .catch(err => console.log(err))
        }
      }  
      return () => clearInterval(interval.current) }
    }, [loading, progress, exercise ]);


    const startTraining = () => {
        setLoading(true)
    }

    const cancelTraining = () => {
      clearInterval(interval.current);
      setProgress(0)
      setLoading(false);

      const data = {
        ...currentExercise.current,
        id: currentExercise.current.name, 
        duration: currentExercise.current.duration * (progress / 100),
        calories: currentExercise.current.calories * (progress / 100),
        date: ( new Date() ).toLocaleDateString().split("."),
        status: "cancelled"
      };
      fetch('https://training-app-d460e.firebaseio.com/exercises.json', {
            method: "POST",
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify(data)

        }).then(result => {
            return result.json();

        })
        .catch(err => console.log(err))

    }



  return (
    <div >
      <AppBar position="static" color="default">
        <Tabs
          value={value}
          onChange={handleChange}
          indicatorColor="primary"
          textColor="primary"
          variant="fullWidth"
          aria-label="full width tabs example"
        >
          <Tab label="Current Training" {...a11yProps(0)} />
          <Tab label="Past Training" {...a11yProps(1)} />

        </Tabs>
      </AppBar>
      <SwipeableViews
        axis={theme.direction === 'rtl' ? 'x-reverse' : 'x'}
        index={value}
        onChangeIndex={handleChangeIndex}
      >
        <TabPanel value={value} index={0} dir={theme.direction}>
            <CurrentTrainingTab
             exercise={exercise}
             handleExerciseChange={handleExerciseChange}
             loading={loading}
             progress={progress}
             cancelTraining={cancelTraining}
             startTraining={startTraining}
              />
        </TabPanel>
        <TabPanel value={value} index={1} dir={theme.direction}>
          <PastTrainingTable finishedExercises={[...finishedExercises.current]} />
        </TabPanel>

      </SwipeableViews>
    </div>
  );
}
