import React from 'react';
import './Current-training-tab.css'
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import Button from '@material-ui/core/Button';
import {exercises} from '../../../Exercises';
import CurrentTraining from '../current-training-timer/Current-training-timer';


const CurrentTrainingTab = () => {

  const [exercise, setExercise] = React.useState('');
  const [startExercise, setStartExercise] = React.useState(false);


  const handleChange = (event) => {
      if(event.target.value !== 0) {
        setExercise(event.target.value);
      }
      return;


  };



  const handleClose = () => {
    setStartExercise(false);
  };

  const handleOpen = () => {
    setStartExercise(true);
  };



    return(
        <div className="selectEx">
        <Button onClick={handleOpen} >
          Open the select
        </Button>
        <FormControl >
          <InputLabel >Exercises</InputLabel>
          <Select
            labelId="demo-controlled-open-select-label"
            id="demo-controlled-open-select"
            open={startExercise}
            onClose={handleClose}
            onOpen={handleOpen}
            value={exercise}
            onChange={handleChange}>    
          {exercises.map(ex => {
              return  <MenuItem value={ex.id} > {ex.id} </MenuItem>
          })} 
          </Select>
        </FormControl>
       <CurrentTraining exercise={exercise} />
      </div>
    ) 
}

export default CurrentTrainingTab;
