    const [progress, setProgress] = React.useState(0);
    const [loading, setLoading] = React.useState(false);

    React.useEffect(() => {

        const timer = setInterval(()=> {
            setProgress(progress => progress + 1)
            if(progress === 5) {
                return clearInterval(timer)
            }

        }, 1000)
        return () => {clearInterval(timer)}





      }, []);

      const start = () => {
          setLoading(true)
      }


    return(
        <div>
           {loading && progress}
