import app from 'firebase/app';

export const config = {
        apiKey: 'AIzaSyDfXm6ZdXTS2shOQHfaO4kAw8qDRESCUXo',
        authDomain: 'training-app-cf8f3.firebaseapp.com',
        databaseURL: 'https://training-app-cf8f3.firebaseio.com>',
        projectId: 'training-app-cf8f3',
        storageBucket: 'training-app-cf8f3.appspot.com',
        messagingSenderId: '209321811851'

}

const Firebase = () => {

    app.initializeApp(config);
    const auth = app.auth();

    const createUser = (email, password) => {
        auth.createUserWithEmailAndPassword(email, password);
    }

    const loginUser = (email, password) => {
        auth.signInWithEmailAndPassword(email, password);
    }

    const signOut = () => {
        auth.signOut();
    }

}

export default Firebase
