import React, { useContext } from 'react';
import './AppBar.css'
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import { Link } from 'react-router-dom';
import { AuthContext } from '../../auth/AuthContext';
import firebase from '../../firebase/firebase'
import 'firebase/auth';
import PersistentDrawerLeft from '../drawer/Drawer';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
}));

export default function () {

  const classes = useStyles();
  const {authenticated } = useContext(AuthContext);


  const onLogOut = () => {
    firebase.auth().signOut()
 }

  return (
    <div className={classes.root}>
      <AppBar position="static">
      <PersistentDrawerLeft />

        <Toolbar>
          <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="menu">

            <MenuIcon />
          </IconButton>
          <h1>My App</h1>
          <div className="spacer"></div>
          {!authenticated &&
           <Link to="/signup"><Button className="headerLink" >Signup</Button></Link> } 
          {!authenticated ?
           <Link to="/login"><Button className="headerLink" >Login</Button></Link>:
           <Link to="/login"><Button onClick={onLogOut} className="headerLink" >Logout</Button></Link> }
          {authenticated &&
           <Link to="/training-tab"><Button className="headerLink" >Training</Button></Link> }
        </Toolbar>
      </AppBar>
    </div>
  );
}
